inline bool isDiagonalVector(BigDataStatMeth::hdf5Dataset* ds)
        {
            hsize_t rows = ds-&gt;nrows();
            hsize_t cols = ds-&gt;ncols();

            return (rows == 1 && cols &gt; 1) ||     // Row vector
                (cols == 1 && rows &gt; 1) ||     // Column vector
                (rows == 1 && cols == 1);      // Scalar
        }

