inline matrix transpose( const matrix &M )
    {
        int rows = M.size();
        int cols = M[0].size();

        matrix T( cols, std::vector&lt;double&gt;( rows ) );

        for ( int i = 0; i &lt; rows; i++ )
        {
            for ( int j = 0; j &lt; cols; j++ ) T[j][i] = M[i][j];
        }

        return T;
    }

