            if (bycols == true) {
                ilimit = dims_out[0];
                count[1] = dims_out[1];
                offset[1] = 0;
            } else {
                ilimit = dims_out[1];
                count[0] = dims_out[0];
                offset[0] = 0;
            }

            for( int i=0; i &lt;= (ilimit/blocksize); i++) 
            {
                int iread;
                int iblockrem = 0;

                if( (i+1)*blocksize &lt; ilimit) iread = blocksize;
                else iread = ilimit - (i*blocksize);

                if(bycols == true) {
                    count[0] = iread; 
                    offset[0] = i*blocksize;
                } else {
                    count[1] = iread; 
