        {
            if( ii + blockSize &gt; maxPosition ) {
                isize = maxPosition - ii; }

            hsize_t sizetoRead = getOptimBlockSize( maxPosition, blockSize, ii, isize);

            starts.push_back(ii);
            sizes.push_back(sizetoRead);

            if( sizetoRead &gt; blockSize ) {
                ii = ii - blockSize + sizetoRead; }
        }

    }

