inline Eigen::VectorXd cumsum(Eigen::VectorXd x)
    {
        // initialize an accumulator variable
        double acc = 0;
        // initialize the result vector
        Eigen::VectorXd res = Eigen::VectorXd::Zero(x.size());
        for(int i = 0; i &lt; x.size(); i++){
            acc += x[i];
            res[i] = acc;
        }
        return res;
