            if (rows == 1 && cols &gt; 1) {
                return cols;  // Row vector 1√óN
            } else if (cols == 1 && rows &gt; 1) {
                return rows;  // Column vector N√ó1
            } else if (rows == 1 && cols == 1) {
                return 1;     // Scalar 1√ó1
            }
            return 0;  // Not a vector (matrix N√óM)
        }

